<script lang="ts" setup>
const animals = [
    { x: 0, y: 200, name: 'Bell_Demo_CSS_only', order: 2, clicked: false },
    { x: 200, y: 400, name: 'saru', order: 4, clicked: false },
    { x: 400, y: 200, name: 'tora', order: 3, clicked: false },
    { x: 600, y: 300, name: 'zou', order: 1, clicked: false },
    { x: 800, y: 200, name: 'kame', orfer: 5, clicked: false },
];
const stage = useState('stage', () => 'start');
const scoreMax = animals.length;
const scoreCurrent = useState('scoreCurrent', () => scoreMax);
const lifeMax = 3;
const lifeCurrent = useState('lifeCurrent', () => lifeMax);
const sorted = computed(() => [...animals].sort((a, b) => a.order - b.order));
const config = useRuntimeConfig();
const svg = (name:string) => `${config.app.baseURL}images/${name}.svg`;

const click = (animal) => {
    if (sorted.value.find(i => i == animal)?.clicked === true) return;
    const remains = sorted.value.filter(i => !i.clicked);
    if (remains[0] === animal) {
        animal.clicked = true;
        scoreCurrent.value--;
        if (remains.length <= 1) stage.value = 'clear';
    } else {
        lifeCurrent.value--;
        if (lifeCurrent.value <= 0) stage.value = 'gameover';
    }
}
watch(stage, value => {
    if (value == 'start') {
        scoreCurrent.value = scoreMax;
        lifeCurrent.value = lifeMax;
        animals.forEach(i => i.clicked = false);
    }
})
</script>

